var scategorieSchema = mongoose.Schema({
  nomscategorie_1: {
    type: String,
    required: false
  },
  imagescat: {
    type: String,
    required: false
  },
  categorieID: {
    type: mongoose.Schema.Types.ObjectId,
    ref: Categorie
  }
});
var Scategorie = mongoose.model('Scategorie', scategorieSchema);
module.exports = Scategorie;
